import React from 'react'
import "./Logs.css"
import data from "../data/sample.json"

export const Logs = ({ ftype, flever, fsearch, ffrom, fto }) => {

    // function to auto-detect log type
    const getLogType = (message) => {
        const keywords = ["error", "failed", "exception", "timeout"]
        const lowerMsg = message.toLowerCase()
        for (let keyword of keywords) {
            if (lowerMsg.includes(keyword)) {
                return "Anomaly"
            }
        }
        return "Normal"
    }

    return (
        <div className='log-container'>
            <div className='log-heading'>
                <h1>Logs</h1>
            </div>
            <div className='logs'>
                {
                    data.map((log, index) => {
                        const handleshowMore = () => {
                            const desc = document.getElementsByClassName('log-description')[index]
                            if (desc.style.display === "block") {
                                desc.style.display = "none"
                            }
                            else {
                                desc.style.display = 'block'
                            }
                        }

                        // detect log type dynamically
                        const logType = getLogType(log.message)

                        // Convert timestamps for date range filtering
                        const logTime = new Date(log.timestamp).getTime()
                        const fromTime = ffrom ? new Date(ffrom).getTime() : null
                        const toTime = fto ? new Date(fto).getTime() : null

                        const inDateRange =
                            (!fromTime || logTime >= fromTime) &&
                            (!toTime || logTime <= toTime)

                        if (
                            (ftype === "All" || logType === ftype) &&
                            (flever === "All" || log.level === flever) &&
                            (fsearch === "" || log.message.toLowerCase().includes(fsearch.toLowerCase())) &&
                            inDateRange
                        ) {
                            return (
                                <div className='log' key={index} onClick={handleshowMore}>
                                    <div className='log-heading'>
                                        <span className=''>{log.level}</span>
                                        <span>{log.timestamp}</span>
                                    </div>
                                    <div className='log-message'>
                                        <p>{log.message}</p>
                                    </div>
                                    <div className='log-info'>
                                        <span>Resource:</span>
                                        <span>{log.resourceId}</span>
                                        <span> | </span>
                                        <span>Commit:</span>
                                        <span>{log.commit}</span>
                                    </div>
                                    <div className='log-description'>
                                        <div><span>Trace Id:</span><span>{log.traceId}</span></div>
                                        <div><span>Span Id:</span><span>{log.spanId}</span></div>
                                        <div><span>Commit:</span><span>{log.commit}</span></div>
                                        <div><span>Type:</span><span>{logType}</span></div> {/* ✅ new line */}
                                    </div>
                                </div>
                            )
                        }
                        return null
                    })
                }
            </div>
        </div>
    )
}
